var uniqueRandomArray = require('unique-random-array');
var superHeroesName = require('./superheroes-name.json');
var getRandomItems = uniqueRandomArray(superHeroesName);

module.exports = {
  all: superHeroesName ,
  random: random
};

function random(number) {
  if(number === undefined){
    return getRandomItems();
  } else {
    var randomItems = [];
    for (var i = 0; i < number; i++){
      randomItems.push(getRandomItems());
    }
    return randomItems;
  }
}
