var expect = require('chai').expect;
var superHero = require('./index');

describe('superheroes-name', function() {
  describe('all', function() {
    it('should be an array of string', function() {
      expect(superHero.all).to.satisfy(isArrayOfString);

      function isArrayOfString(array) {
        return array.every(function(item) {
          return typeof item === 'string';
        });
      }
    });

    it('should contain `Chacha Chaudhary`', function() {
      expect(superHero.all).to.include('Chacha Chaudhary');
    });
  });

  describe('random', function() {
    it('it should return a random item from superHero list', function() {
      var randomItem = superHero.random();
      expect(superHero.all).to.include(randomItem);
    });
    it('should return an array of random item if passed a number', function() {
      var randomItems = superHero.random(3);
      randomItems.forEach(function(item) {
        expect(superHero.all).to.include(item);
      });
    });
  });
});
